import React, { useState } from "react";
import { Button, FormGroup, FormControl, FormLabel, FormText, FormCheck } from "react-bootstrap";
import "./Login.css";


class LoginPage extends React.Component {

  constructor() {
    super()
    this.state = 
      {
        email: "",
        password: ""
      }

    this.validateForm = this.validateForm.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  validateForm() {
    return this.state.email.length > 0 && this.state.password.length > 0;
  }

  handleSubmit(event) {
    event.preventDefault();
  }
  render() {
    return (
      <div className="Login" class="col-lg-12 text-center">
        <h3 id="LoginTitle">Login to EasyA!</h3>
        <form onSubmit={this.handleSubmit}>
            <FormGroup controlId="formBasicEmail">
              <FormLabel id="enterEmail">Email address</FormLabel>
              <FormControl type="email" placeholder="Enter email" />
              <FormText className="text-muted">
                We'll never share your email with anyone else.
              </FormText>
            </FormGroup>

            <FormGroup controlId="formBasicPassword">
              <FormLabel>Password</FormLabel>
              <FormControl type="password" placeholder="Password" />
            </FormGroup>
            <FormGroup controlId="formBasicCheckbox">
              <FormCheck type="checkbox" label="Check me out" />
            </FormGroup>
            <Button size="lg" disabled={!this.validateForm} type="submit">
              Login
            </Button>
        </form>
      </div>
    );
  }

}
export default LoginPage;
