import React from 'react';
//import './App.css';
import Navbar from './components/Navbar/Navbar';
import FirstPrompt from './components/Home/FirstPrompt';
import MainWelcome from './components/Home/MainWelcome';
import "./css/app.css";
import Login from './components/Login/LoginPage';
import About from "./components/About/AboutPage";
import Contact from "./components/Contact/ContactPage";
import SignUp from './components/SignUp/SignUp'
import UserPage from './components/User/UserPage';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

const PrivateRoute = ({ component: Component, ...rest }) => (

  <Route {...rest} render={(props) => (
      localStorage.token ? <Component {...props} />
      : <Redirect to="/login"/>    
  )}/>
)
/**
 * App.js organizes all components to be rendered and exports them to index.js. 
 */
class App extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      profile_data: ""
    }

  }
  liftStateUp = (data) => 
  {
    console.log("DATA")
    console.log(data);
    this.setState({data: data})
  }
  render() {
    return (
      <div className="App">
        {/* Always include Navbar on top */ }    

        <Router>
          { /* Route to main page */ }
          <Route exact path = "/">
            <header className="App-header"> 
                <Navbar />     
                <MainWelcome profile_data={this.state}/>
                <FirstPrompt />
            </header>
          </Route>

          { /* Route to login page */ }
          <Route exact path = "/login">
            <Navbar />
            <Login liftStateUp={this.liftStateUp}/>
          </Route>

          { /* Route to about page. */ }
          <Route exact path = "/about">     
            <Navbar />
            <About />
          </Route>

          { /* Route to contact page. */ }
          <Route exact path = "/contact">
            <Navbar />
            <Contact />
          </Route>

          {/* Route to signup page. */}
          <Route exact path = "/signup">
            <Navbar />
            <SignUp />
          </Route>

          {/* Route to user homepage */}
          <PrivateRoute path="/homepage">
            <Navbar />
            <UserPage />
          </PrivateRoute>


        </Router>
      </div>
    );
  }
}

export default App;
