import React from 'react';
import ReactDOM from 'react-dom';
import jwt from 'jwt-decode';
import {Button} from 'react-bootstrap';

class UserPage extends React.Component {

    constructor() {
        super();
        //this.renderWelcome();
        this.vie
    }

    viewWishlist() {
        const GEP = this;
        let url = "http://localhost:5000/gep";
        fetch( 
            url, {
                method: "GET",
                //headers: {"GEP": document.getElementById("gep_list_select").value, "term": this.props.whichterm}
                headers: {"GEP": this.state.select_value, "term": this.props.whichterm}
           }
        ).then(
           response => response.json()
        ).then(
            data => this.setState({
                courses: data
            },
            () => ReactDOM.render(<Table data={this.state.courses} />, document.getElementById('id_table'))
            )
        )
    }
    render() {
        const orgName = "EasyA";
        let userwelcome;
        //console.log("PROPS");
        //console.log(this.props.profile_data);
        if (localStorage.token) {
            let identity = jwt(localStorage.token).identity;
            userwelcome = <h1 id="welcome" class="mt-5">Welcome to your account, <strong>{identity.first_name} {identity.last_name}</strong>!</h1>
        }

        return(
                <div class="col-lg-12 text-center">
                    {userwelcome}
                    {/*<p class="lead"><strong>For Engineers, By Engineers</strong></p>*/}
                    <p id="lab" class="lead">Here, you can customize your course selection preferences and personalize your EasyA results!</p>

                    <Button
                    class="btn btn-danger" 
                            id="whichgep" 
                            name="whichgep" 
                            onClick={this.viewWishlist}
                            size="lg">
                        View My Wishlist
                    </Button>
                </div>
        );
    }
}

export default UserPage;
