  "warning": { "text": "#fff", "background": "#F0AD4E", "border": "transparent", "arrow": "#F0AD4E" },
  "error": { "text": "#fff", "background": "#BE6464", "border": "transparent", "arrow": "#BE6464" },
  "info": { "text": "#fff", "background": "#337AB7", "border": "transparent", "arrow": "#337AB7" },
