import React from 'react';
import ReactDOM from 'react-dom';
import jwt from 'jwt-decode';
import {Button} from 'react-bootstrap';
import Table from '../Home/Table';
import './userpage.css';

class UserPage extends React.Component {

    constructor() {
        super();
        //this.renderWelcome();
        this.viewWishlist = this.viewWishlist.bind(this);
    }

    viewWishlist() {
        const UserPage = this;
        let url = "http://localhost:5000/viewWishlist";
        fetch( 
            url, {
                method: "GET",
                headers: {"token": localStorage.token }
            }
        ).then(
           response => response.json()
        ).then(
            data => this.setState({
                courses: data
            },
            () => {ReactDOM.render(<Table data={data} type="homepage" />, document.getElementById('wishlist'))}
            )
        )
    }

    render() {
        const orgName = "EasyA";
        let userwelcome;
        //console.log("PROPS");
        //console.log(this.props.profile_data);
        if (localStorage.token) {
            let identity = jwt(localStorage.token).identity;
            userwelcome = <h1 id="welcome" class="mt-5">Welcome to your account, <strong>{identity.first_name} {identity.last_name}</strong>!</h1>
        }

        return(
                <div class="col-lg-12 text-center">
                    {userwelcome}
                    {/*<p class="lead"><strong>For Engineers, By Engineers</strong></p>*/}
                    <p id="lab" class="lead">Here, you can customize your course selection preferences and personalize your EasyA results!</p>

                    <div id="wishlistbtn">
                        <Button
                        class="btn btn-danger" 
                                id="mywishlist" 
                                name="mywishlist" 
                                onClick={this.viewWishlist}
                                size="lg">
                            My Wishlist
                        </Button>
                    </div>

                    <div id="wishlist">

                    </div>
                </div>
        );
    }
}

export default UserPage;
