//imports
import React from 'react';
import ReactDOM from 'react-dom';
import Table from '../Home/Table';

class Wishlist extends React.Component {

    constructor() {
        super();

        this.viewWishlist();
    }

    viewWishlist() {
        const UserPage = this;
        let url = "http://localhost:5000/viewWishlist";
        fetch( 
            url, {
                method: "GET",
                headers: {"token": localStorage.token }
            }
        ).then(
        response => response.json()
        ).then(
            data => this.setState({
                courses: data
            },
            () => {ReactDOM.render(<Table data={this.state.courses} type="homepage" />, document.getElementById('wishlist'))}
            )
        )
    }

    render() {
        let identity = jwt(localStorage.token).identity;
        return (
            <div>
                <h1 id="welcome" class="mt-5"><h1 id="welcome" class="mt-5"><strong>{identity.first_name}'s</strong>Wishlist</h1>!</h1>
                <div id="wishlist">

                </div>
            </div>
        )
    }
}
export default Wishlist;
