class LoginPage extends React.Component {
  render () {
    return (

      <div>
        <div>
          <title>Login V11</title>
        </div>
        <div>
          <div className="limiter">
            <div className="container-login100">
              <div className="wrap-login100 p-l-50 p-r-50 p-t-77 p-b-30">
                <form className="login100-form validate-form">
                  <span className="login100-form-title p-b-55">
                    Login to EasyA
                  </span>
                  <div className="wrap-input100 validate-input m-b-16" data-validate="Valid email is required: ex@abc.xyz">
                    <input className="input100" type="text" name="email" placeholder="Email" />
                    <span className="focus-input100" />
                    <span className="symbol-input100">
                      <span className="lnr lnr-envelope" />
                    </span>
                  </div>
                  <div className="wrap-input100 validate-input m-b-16" data-validate="Password is required">
                    <input className="input100" type="password" name="pass" placeholder="Password" />
                    <span className="focus-input100" />
                    <span className="symbol-input100">
                      <span className="lnr lnr-lock" />
                    </span>
                  </div>
                  <div className="contact100-form-checkbox m-l-4">
                    <input className="input-checkbox100" id="ckb1" type="checkbox" name="remember-me" />
                    <label className="label-checkbox100" htmlFor="ckb1">
                      Remember me
                    </label>
                  </div>
                  <div className="container-login100-form-btn p-t-25">
                    <button className="login100-form-btn">
                      Login
                    </button>
                  </div>
                  <div className="text-center w-full p-t-42 p-b-22">
                    <span className="txt1">
                      Or login with
                    </span>
                  </div>
                  <a href="#" className="btn-face m-b-10">
                    <i className="fa fa-facebook-official" />
                    Facebook
                  </a>
                  <a href="#" className="btn-google m-b-10">
                    <img src="./static/images/icons/icon-google.png" alt="" />
                    Google
                  </a>
                  <div className="text-center w-full p-t-115">
                    <span className="txt1">
                      Not a member?
                    </span>
                    <a className="txt1 bo1 hov1" href="#">
                      Sign up now							
                    </a>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
export default LoginPage;
