// incl First prompt text, first button
// dropdown will be generalized
import React from 'react';
import './css/index.css';
import ReactDOM from 'react-dom';

class MainWelcome extends React.Component {

    constructor(props) {
        super(props);

        this.renderWelcome();
    }

    renderWelcome() {
        let name = this.props.user_data.givenName;
        if (this.props.user_data.givenName) {
            ReactDOM.render(
                <h1 id="welcome" class="mt-5">Welcome to EasyA, {name}!</h1>
            ,
            document.getElementById("userwelcome")
            )
        }
        else {
            ReactDOM.render (
                <h1 id="welcome" class="mt-5">Welcome to EasyA!</h1>,
                document.getElementById("userwelcome")
            )
        }
    }
    render() {
        return(
                <div class="col-lg-12 text-center" id="userwelcome">
                    {/*<p class="lead"><strong>For Engineers, By Engineers</strong></p>*/}
                    <p id="lab" class="lead">This tool allows you to find which NC State GEP or elective courses will be the best for you to take!</p>
                </div>
        );
    }
}

export default MainWelcome;
