import React from 'react';
import Select from 'react-select';
import ReactDOM from 'react-dom';

// need to do:
    // obtain value of selected dept
    // send value of dept to CourseLevel
    // get list of depts from backend
class Dept extends React.Component {
    constructor() {
        super();
        this.getDepts = this.getDepts.bind(this)
        this.DeptList = this.DeptList.bind(this)

        this.setState({
            dept_list: null
        })
        /* Call depts function */
        this.getDepts();
    }

    getTable() {
        const GEP = this;
        let url = "http://localhost:5000/dept";
        fetch( 
            url, {
                method: "GET",
                headers: {
                    "Dept": document.getElementById("deptlist").value,
                    "term": this.props.whichterm,
                    "level": document.getElementById("levelprompt").value
                }
           }
        ).then(
           response => response.json()
        ).then(
            data => this.setState({
                courses: data
            },
            () => ReactDOM.render(<Table data={this.state.courses} />, document.getElementById('id_table'))
            )
        )
    }

    /* Get List of departments and render select component */
    getDepts() {
        const GEP = this;
        let url = "http://localhost:5000/getdepts";
        return fetch( 
            url, {
                method: "GET"
           }
        ).then(
            response => response.json() // keep here (maybe)
        ).then(
            (data) => {this.setState({dept_list: data},
                () => ReactDOM.render(this.DeptList(), document.getElementById('deptlist'))
            )}
        )
    }
    
    /* Return select component with list of departments */
    DeptList(){
        const departments = this.state.dept_list;
        //console.log(departments);
        const deptOptions = departments.dept.map((dept) => (
            {label: dept}
        ));
        const deptValues = departments.dept_code.map((value_i) => (
            {value: value_i}
        ));
        const deptFinal = [];
        for (var i = 0; i < deptOptions.length; i++) {
            deptFinal[i] = {label: deptOptions[i].label,
                            value: deptValues[i].value}
        }
        //console.log(deptFinal);
        return (
        <div className="app">
            <div className="container">
                <Select id='deptList' options={deptFinal} onChange={department => console.log(department.label, department.value)} /> {/* add attribute onChange={department => ___} */}
            </div>
        </div>
        )
    };

    render() {
    
        /* Save list of levels and options for dropdown */
        const levelList = ["100", "200", "300", "400", "500", "600", "700","800","ANY"];
        const levelOptions = levelList.map((level) => (
            {label: level}
        ));

        return(
            <div id="typeofdept" class="text-center">
                <h2 class="mt-5">Select a Department</h2>
                {/* Gives options for specific department the user is looking for */}
                <label for="typeofdept">Please select the department of the course/elective you are looking for.</label>
                {/* <select id="typeofdept" name = "typeofdept" class="bg-light"> */}
                    {/* <!-- TOBEIMPLEMENTED--> */}

                    <div id="deptlist">

                    </div>
                
                {/* prompt for levels */ }
                <div id="levels">
                    <label for="levelprompt">Please select the level of the course/elective you are looking for.</label>
                    <div className="container">
                        <Select id='levelprompt' options={levelOptions} /> {/* add attribute onChange={department => ___} */}
                    </div>
                </div>

                {/* </select> */}
                <button type="button" class="btn btn-danger" id="whichdept" name="whichdept" onclick={this.getTable()}>Select</button>

                { /* Div for course table */ }
                <div id="id_table">

                </div>
            </div>
        );
    }
}
{/* <DeptList selectedDept={document.getElementById('deptList')} /> */}
export default Dept;
